import React, { useEffect, useState } from 'react';
import SampleComponent from 'components/sample/SampleComponent';
import { withRouter } from 'react-router-dom';
import axios from 'axios';
import { useHistory, useParams } from 'react-router-dom';
import StarRating from './StarRating.js';
import Pie from './Pie.js';
import BarGraph from './Bar.js';


const DetailPresenter = (props) => {
  const [movie, setMovie] = useState({
      "movie_id": 10001,
      "movie_title": "시네마 천국",
      "movie_url": "https://movie.naver.com/movie/bi/mi/basic.naver?code=10001",
      "movie_aud": 9.63,
      "movie_net": 9.33,
      "movie_rep": 8.38,
      "movie_poster": "https://movie-phinf.pstatic.net/20210409_134/16179547654813MHq5_JPEG/movie_image.jpg?type=m203_290_2",
      "movie_genre": 2,
      "movie_country": 10,
      "movie_runtime": 124,
      "movie_release": "2020-04-22",
      "movie_director": "쥬세페 토르나토레",
      "movie_age": 4,
      "movie_content": "어린 시절 영화가 세상의 전부였던 소년 토토는 학교 수업을 마치면 마을 광장에 있는 낡은 시네마 천국이라는 극장으로 달려가 영사 기사 알프레도와 친구로 지내며 어깨너머로 영사기술을 배운다. 어느 날 관객들을 위해 광장에서 야외 상영을 해주던 알프레도가 그만 화재 사고로 실명하게 되고 토토가 그의 뒤를 이어 시네마 천국의 영상기사로 일하게 된다. 실명한 후에도 토토의 친구이자 아버지로 든든한 정신적 지주가 되어준 알프레도는 청년이 된 토토가 사랑하는 여자 엘레나의 부모님의 반대로 좌절하자 넓은 세상으로 나가서 더 많은 것을 배우라며 권유하는데...",
      "comment": [

      ]
  })
  useEffect(() => {  
    const loadMovies = async() => {
      const config = {
                headers: {
                  "content-type": "application/json"
                }
            };
            await axios.get(`/api/${props.match.params.id}/`, config).then((response)=>{ 
                setMovie(response.data)
            }); 
    }
    loadMovies();
  }, []);

    let history = useHistory();
    return(
      <div className='detail-content'>    
        <div>
          <img onClick={()=>{history.goBack() }} src="img/back.svg" alt="" />
        </div>  
        <div>
          <div>
            <div className='detail-box'>
              <div className='col-md-7 title-box'>
                <h1>{movie.movie_title}</h1>
                <br/>
                <h5>        
                  {movie.movie_genre} / 
                  {movie.movie_country} / 
                  {movie.movie_runtime+"분"} / 
                  {movie.movie_release} / 
                  {movie.movie_age}
                </h5>
                <br/>
                <h5>{"감독: "+movie.movie_director}</h5>
                <br/>
                <h5>출연</h5>
                <br/>
                <h6>{movie.movie_url}</h6>
              </div>
              <div className='col-md-5 detail-poster'>
                <img className='img-detail' src={movie.movie_poster} width='100%' alt='movie1'/>
                {/* <img className='img-responsive' src="img/movie_image{props.movie_data.title}.jpg" width='100%'/> */}
              </div>
            </div>
            <br/>
            <div className='sex_age_graph'>
              <div className='Graph_1'>
                {/* <Pie movie={movie}/> */}
              </div> 
              <div className='Graph_2'>
                {/* <BarGraph movie={movie}/> */}
              </div>
              <div className='col-md-12 Graph_3'>
                <div className='circle-m'></div>
                  <p className='desc-m'>남자</p>
                  <div className='circle-f'></div>
                  <p className='desc-f'>여자</p>
                </div>
              </div>
              <br/>
              <div className='col-md-7 synopsis'>
                <h4>줄거리</h4>
                <br/>
                <div className='summary'>
                  <p>{movie.movie_content}</p>
                </div>
              </div>
              <br/>
              <div className='Tag-box col-md-7'>
                <h5>리뷰 태그</h5>
              </div>
              <br/>
              <div className='review-box col-md-7'>
                <h4>평점/후기</h4>
                {movie.comment.map((m,i)=>{
                  return(
                                    /* <StarRating movie={movie}/> {m.comment_point}/10 */
                    <li className='comment_list'>
                      <StarRating movie={movie} i={i}/> {movie.comment[i].comment_point}/10
                      <h5>{m.comment_username}</h5>
                      <h5>{m.comment_contents}</h5>
                      <br/>
                    </li>
                  )
                })} 
              </div>
            </div>
          </div> 
          <br/>
          <div className='button-box'>
            {/* <button><Link to={movie-Detail.link}> 넷플릭스에서 보기 </Link></button>
            <button><Link to={movie-Detail.link}> 왓챠에서 보기 </Link></button> */}
            <button className='btn netflix'> 넷플릭스에서 보기 </button>
            <button className='watcha btn'> 왓챠에서 보기 </button>
          </div>
        </div>
    );
};

export default withRouter(DetailPresenter);
